import '../../theme/theme.dart';class UserQualificationBody extends StatefulWidget {  const UserQualificationBody({Key? key}) : super(key: key);  @override  State<UserQualificationBody> createState() => _UserQualificationBodyState();}class _UserQualificationBodyState extends State<UserQualificationBody> {  @override  Widget build(BuildContext context) {    return SpacedColumn(        mainAxisAlignment: MainAxisAlignment.spaceBetween,        crossAxisAlignment: CrossAxisAlignment.start,        children: [_buildTopSec(), _buildBottomSec()]);  }  Widget _buildTopSec() {    double dpWidth = 150;    double inputWidth = 344;    return SizedBox(        height: 80,        child: SpacedColumn(            crossAxisAlignment: CrossAxisAlignment.center,            mainAxisAlignment: MainAxisAlignment.center,            verticalSpace: 8.0,            children: [              Padding(                  padding: const EdgeInsets.symmetric(                    horizontal: 16.0,                    vertical: 8.0,                  ),                  child: SpacedRow(                      crossAxisAlignment: CrossAxisAlignment.center,                      mainAxisAlignment: MainAxisAlignment.spaceBetween,                      children: [                        TextInputWidget(                          width: inputWidth,                          heigth: 48,                          leftIcon: HeroIcons.search,                          hintText: 'Search ...',                          controller: TextEditingController(),                        ),                        SizedBox(                          height: 48,                          child: DropdownWidget(                              hintText: "Action",                              isRequired: true,                              dropdownBtnWidth: dpWidth,                              dropdownOptionsWidth: dpWidth,                              onChanged: (val) {},                              items: const ["Basic", "Premium", "Enterprise"]),                        )                      ])),              const Divider(height: 0, color: ThemeColors.gray11),            ]));  }  Widget _buildBottomSec() {    return Container(      height: 750 - 80,    );  }}