import 'package:auto_route/auto_route.dart';import 'package:flutter_svg/svg.dart';import '../../manager/models/timesheet_dep_md.dart';import '../../theme/theme.dart';class CheckListCommentsDrawer extends StatefulWidget {  const CheckListCommentsDrawer({Key? key}) : super(key: key);  @override  State<CheckListCommentsDrawer> createState() =>      _CheckListCommentsDrawerState();}class _CheckListCommentsDrawerState extends State<CheckListCommentsDrawer> {  @override  void initState() {    super.initState();    print('CheckListCommentsDrawer: initState');  }  @override  void dispose() {    print('CheckListCommentsDrawer: dispose');    super.dispose();  }  @override  Widget build(BuildContext context) {    return Drawer(      width: 640,      backgroundColor: ThemeColors.white,      elevation: 0.8,      child: SpacedColumn(        children: [_header(), _body()],      ),    );  }  Widget _header() {    return Padding(        padding: const EdgeInsets.all(24.0),        child: SpacedColumn(          verticalSpace: 24.0,          children: [            SpacedRow(                mainAxisAlignment: MainAxisAlignment.spaceBetween,                children: [                  Column(children: [                    SizedBox(                        width: 440,                        child: KText(                            isSelectable: false,                            text: "Comments",                            fontSize: 18.0,                            textColor: ThemeColors.gray2,                            fontWeight: FWeight.bold)),                    SizedBox(                        width: 440,                        child: KText(                            isSelectable: false,                            text: "Flat 26, 102 Westminster SE1 7XT",                            fontSize: 14.0,                            textColor: ThemeColors.gray2,                            fontWeight: FWeight.regular))                  ]),                  InkWell(                      child: const HeroIcon(                        HeroIcons.x,                        size: 16.0,                        color: ThemeColors.gray1,                      ),                      onTap: () {                        context.popRoute();                      }),                ]),            const SizedBox(                width: 580,                child: Divider(height: 1, color: ThemeColors.gray11)),          ],        ));  }  Widget _body() {    return Padding(        padding: const EdgeInsets.all(24.0),        child: SpacedColumn(children: [          bodyHelper(              leftText: "Emmanuella Gamble",              rightText: "9:30AM, 22/07/2022",              icon: HeroIcon(HeroIcons.user),              description:                  "Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum."),          bodyHelper(              leftText: "Coby Dennis",              icon: HeroIcon(HeroIcons.user),              rightText: "9:30AM, 22/07/2022",              description:                  "Sed ut perspiciatis unde omnis iste natus error sit voluptatem accusantium doloremque laudantium."),        ]));  }  Widget bodyHelper(      {Widget? icon,      required String leftText,      required String rightText,      required String description}) {    return Padding(        padding: const EdgeInsets.symmetric(vertical: 8),        child: SpacedColumn(verticalSpace: 24, children: [          Text(description,              style: const TextStyle(                  color: ThemeColors.gray2, fontSize: 18, height: 1)),          SpacedRow(            crossAxisAlignment: CrossAxisAlignment.center,            mainAxisAlignment: MainAxisAlignment.spaceBetween,            children: [              SpacedRow(                  horizontalSpace: 16.0,                  crossAxisAlignment: CrossAxisAlignment.center,                  children: [                    if (icon != null)                      icon                    else                      SvgPicture.asset('assets/images/robot.svg',                          width: 20, height: 20, color: ThemeColors.gray2),                    KText(                        text: leftText,                        textColor: ThemeColors.gray2,                        fontWeight: FWeight.regular,                        fontSize: 16)                  ]),              KText(                  text: rightText,                  textColor: ThemeColors.gray5,                  fontWeight: FWeight.regular,                  fontSize: 16)            ],          ),          const Divider(height: 1, color: ThemeColors.gray11),        ]));  }}