import 'package:auto_route/auto_route.dart';import 'package:flutter_svg/svg.dart';import '../../manager/models/timesheet_dep_md.dart';import '../../theme/theme.dart';class CheckListDamagesDrawer extends StatefulWidget {  const CheckListDamagesDrawer({Key? key}) : super(key: key);  @override  State<CheckListDamagesDrawer> createState() => _CheckListDamagesDrawerState();}class _CheckListDamagesDrawerState extends State<CheckListDamagesDrawer> {  @override  void initState() {    super.initState();    print('CheckListDamagesDrawer: initState');  }  @override  void dispose() {    print('CheckListDamagesDrawer: dispose');    super.dispose();  }  @override  Widget build(BuildContext context) {    return Drawer(      width: 640,      backgroundColor: ThemeColors.white,      elevation: 0.8,      child: SpacedColumn(        children: [_header(), _body()],      ),    );  }  Widget _header() {    return Padding(        padding: const EdgeInsets.all(24.0),        child: SpacedColumn(          verticalSpace: 24.0,          children: [            SpacedRow(                mainAxisAlignment: MainAxisAlignment.spaceBetween,                children: [                  Column(children: [                    SizedBox(                        width: 440,                        child: KText(                            isSelectable: false,                            text: "Damages",                            fontSize: 18.0,                            textColor: ThemeColors.gray2,                            fontWeight: FWeight.bold)),                    SizedBox(                        width: 440,                        child: KText(                            isSelectable: false,                            text: "Flat 26, 102 Westminster SE1 7XT",                            fontSize: 14.0,                            textColor: ThemeColors.gray2,                            fontWeight: FWeight.regular))                  ]),                  InkWell(                      child: const HeroIcon(                        HeroIcons.x,                        size: 16.0,                        color: ThemeColors.gray1,                      ),                      onTap: () {                        context.popRoute();                      }),                ]),            const SizedBox(                width: 580,                child: Divider(height: 1, color: ThemeColors.gray11)),          ],        ));  }  Widget _body() {    return Padding(        padding: const EdgeInsets.all(24.0),        child: SpacedColumn(verticalSpace: 16, children: [          KText(            text: "Bathroom",            textColor: ThemeColors.gray2,            fontWeight: FWeight.bold,            fontSize: 18,          ),          KText(            text: "Broken sink, Scratched vanity unit, Broken shower head",            textColor: ThemeColors.gray2,            fontWeight: FWeight.regular,            fontSize: 16,          ),          _buildImgList(),          const SizedBox(              width: 580, child: Divider(height: 1, color: ThemeColors.gray11)),          KText(            text: "Kitchen",            textColor: ThemeColors.gray2,            fontWeight: FWeight.bold,            fontSize: 18,          ),          KText(            text: "Scratched refrigerator",            textColor: ThemeColors.gray2,            fontWeight: FWeight.regular,            fontSize: 16,          ),          _buildImgList(),          const SizedBox(              width: 580, child: Divider(height: 1, color: ThemeColors.gray11)),        ]));  }  Widget _buildImgList() {    List<Widget> list = [];    for (int i = 0; i < 3; i++) {      list.add(ClipRRect(        borderRadius: BorderRadius.circular(8.0),        child: Image.network(          width: 96,          height: 96,          'https://picsum.photos/250?image=${i + 10}',        ),      ));    }    return SpacedRow(      horizontalSpace: 16.0,      children: list,    );  }  Widget bodyHelper(      {Widget? icon,      required String leftText,      required String rightText,      required String description}) {    return Padding(        padding: const EdgeInsets.symmetric(vertical: 8),        child: SpacedColumn(verticalSpace: 24, children: [          Text(description,              style: const TextStyle(                  color: ThemeColors.gray2, fontSize: 18, height: 1)),          SpacedRow(            crossAxisAlignment: CrossAxisAlignment.center,            mainAxisAlignment: MainAxisAlignment.spaceBetween,            children: [              SpacedRow(                  horizontalSpace: 16.0,                  crossAxisAlignment: CrossAxisAlignment.center,                  children: [                    if (icon != null)                      icon                    else                      SvgPicture.asset('assets/images/robot.svg',                          width: 20, height: 20, color: ThemeColors.gray2),                    KText(                        text: leftText,                        textColor: ThemeColors.gray2,                        fontWeight: FWeight.regular,                        fontSize: 16)                  ]),              KText(                  text: rightText,                  textColor: ThemeColors.gray5,                  fontWeight: FWeight.regular,                  fontSize: 16)            ],          ),          const Divider(height: 1, color: ThemeColors.gray11),        ]));  }}